package com.github.cbismuth.scala4impatient

import org.scalatest.{ FlatSpec, Matchers }

class Ch01Ex06_BigIntComputerSpec extends FlatSpec with Matchers {
  "2 raised to the power of 1024" should "return a huge big integer" in {
    val expected = ""
    val actual: String = BigInt(2).pow(1024).toString()
    actual should be(expected)
  }
}
